import React, { useState } from 'react';
import axios from 'axios';
import './searchBar.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

function SearchBar({ renderResults }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("http://54.90.86.188/query", {
        term: searchTerm,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = response.data;
      const formattedData = data.map((item) => {
        const parsedItem = JSON.parse(item);
        return {
          selected: false,
          number: parsedItem.BP_Number,
          mk_number: parsedItem.MK_Number,
          species: parsedItem.Species,
          matrix: parsedItem.Matrix,
          extraction_method: parsedItem.Extraction_Method,
          internal_standard: parsedItem.Internal_Standard,
          chromatography: parsedItem.Chromatography,
          polarity: parsedItem.Polarity,
        };
      });
      renderResults(formattedData);
    } catch (error) {
      console.error("Error making POST request:", error);
    }
  };

  return (
    <div className="searchBar">
      <div id="bar">
        <InputGroup>
          <Form.Control onChange={handleSearchTermChange} />
          &nbsp;
          <div id="submit">
            <input id="submit-btn" type="submit" value="Search" onClick={handleSubmit} />
          </div>
        </InputGroup>
      </div>
    </div>
  );
}

export default SearchBar;
