import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import mockResponse from './mockResponse.json'

export default function Results({ showResults }) {
  const [results, setResults] = useState([]);
  const resultsRef = useRef(null);

  useEffect(() => {
    /*
    async function fetchData() {

        try {
            const response = await axios.get('/api/results'); // replace this with your API endpoint
            setResults(response.data.results);
        } catch (error) {
            console.error(error);
        }
    }

    fetchData();
    */
    setResults(mockResponse.results);
    resultsRef.current.scrollIntoView({ behavior: 'smooth' }); //scroll to the results
  }, []);

  function handleDownload() {
    const selectedBps = results.filter(bp => bp.selected).map(bp => bp.number);
    // TODO: send the selectedBps to the backend to download
  }

  function handleCheckboxChange(bpNumber) {
    setResults(prevResults => {
      return prevResults.map(bp => {
        if (bp.number === bpNumber) {
          return {
            ...bp,
            selected: !bp.selected
          };
        } else {
          return bp;
        }
      });
    });
  }

  return (
    <div ref={resultsRef}>
      <h1>Results</h1>
      <button onClick={handleDownload}>Download Selected BP Files</button>
      <hr></hr>
      {results.map(bp => (
        <div key={bp.number}>
          <h2>{bp.number}</h2>
          <table>
            <thead>
              <tr>
                <th>MK Number</th>
                <th>Species</th>
                <th>Matrix</th>
                <th>Extraction Method</th>
                <th>Internal Standard</th>
                <th>Chromatography</th>
                <th>Polarity</th>
                <th>Select</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{bp.mk_number}</td>
                <td>{bp.species}</td>
                <td>{bp.matrix}</td>
                <td>{bp.extraction_method}</td>
                <td>{bp.internal_standard}</td>
                <td>{bp.chromatography}</td>
                <td>{bp.polarity}</td>
                <td>
                  <input
                    type="checkbox"
                    checked={bp.selected}
                    onChange={() => handleCheckboxChange(bp.number)}
                  />
                </td>
              </tr>
            </tbody>
          </table>
          <hr></hr>
        </div>
      ))}
    </div>
  );
}
