import React, { useState, useEffect, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import AWS from 'aws-sdk';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';

// Set the region
AWS.config.update({ region: 'us-east-2' });

// Set the credentials
AWS.config.update({
  accessKeyId: "AKIAXMUSNCREO4IEXGET",
  secretAccessKey: "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",
});

export default function Results({ resultsData }) {

  const [results, setResults] = useState([]);
  const resultsRef = useRef(null);
  const [downloadStatus, setDownloadStatus] = useState(null);

  useEffect(() => {
    if (Array.isArray(resultsData.results)) {
      setResults(resultsData.results.map(result => ({...result, selected: false})));
    } else {
      setResults([]);
    }
    resultsRef.current.scrollIntoView({ behavior: 'smooth' }); //scroll to the results
  }, [resultsData]);

  useEffect(() => {
    console.log("Results state:", results);
  }, [results]);


  function generatePresignedUrl(bpNumber, fileType) {
    const s3 = new AWS.S3();
    const params = {
      Bucket: 'merckbucket-123',
      Key: `BP${bpNumber}_${fileType}.` + (fileType === 'pdf' ? 'pdf' : 'xlsx'),
      Expires: 3600, // URL expiration time in seconds
    };

    return s3.getSignedUrl('getObject', params);
  }

  async function downloadAndAddToZip(url, filename, zip) {
    console.log('Downloading:', url, 'Filename:', filename);
    const response = await fetch(url);
    const blob = await response.blob();
    return zip.file(filename, blob);
  }

  async function handleDownload() {
    setDownloadStatus('downloading');
    try{
      const selectedBps = results.filter(bp => bp.selected).map(bp => bp.number);

      for (const bpNumber of selectedBps) {
        const pdfUrl = generatePresignedUrl(bpNumber, 'pdf');
        const excelUrl = generatePresignedUrl(bpNumber, 'excel');

        const zip = new JSZip();

        await Promise.all([
          downloadAndAddToZip(pdfUrl, `BP${bpNumber}_pdf.pdf`, zip),
          downloadAndAddToZip(excelUrl, `BP${bpNumber}_excel.xlsx`, zip),
        ]);

        const zipBlob = await zip.generateAsync({ type: 'blob' });
        saveAs(zipBlob, `BP${bpNumber}_files.zip`);
        await new Promise(resolve => setTimeout(resolve, 1000)); // Wait for 1 second before the next download
      }
      setDownloadStatus('completed');
    } catch (error) {
      console.error('Error during download:', error);
      setDownloadStatus('error');
    }
  }



  function handleCheckboxChange(bpNumber) {
    setResults(prevResults => {
      return prevResults.map(bp => {
        if (bp.number === bpNumber) {
          return {
            ...bp,
            selected: !bp.selected
          };
        } else {
          return bp;
        }
      });
    });
  }

  let downloadStatusMessage;

  if (downloadStatus === 'downloading') {
    downloadStatusMessage = <p>Downloading files, please wait...</p>;
  } else if (downloadStatus === 'completed') {
    downloadStatusMessage = <p>Download completed.</p>;
  } else if (downloadStatus === 'error') {
    downloadStatusMessage = <p>Error: Download failed. Please try again.</p>;
  }

  if (results.length > 0) {
    return (
      <div ref={resultsRef}>
          <div style={{ display: 'flex', justifyContent: 'space-between'}}>
              <div>
                  <h1>Results</h1>
              </div> 
              <div>
              <Button
                onClick={handleDownload}
                style={{ marginRight: "2vw" }}
                color="primary"
              >
                Download Selected Documents
              </Button>
              </div>
              <div>
                {downloadStatusMessage}
              </div>
          </div>
          {results.map(bp => (
          <div key={bp.number}>
              <h2>BP-{bp.number}</h2>
              <table>
                  <thead>
                  <tr>
                      <th>Select</th>
                      <th>MK Number</th>
                      <th>Species</th>
                      <th>Matrix</th>
                      <th>Extraction Method</th>
                      <th>Internal Standard</th>
                      <th>Chromatography</th>
                      <th>Polarity</th>

                  </tr>
                  </thead>
                  <tbody>
                  <tr>
                  <td>
                      <input
                          style={{ height: "24px", width: "24px", marginRight : "6vw" }}
                          type="checkbox"
                          checked={bp.selected}
                          onChange={() => handleCheckboxChange(bp.number)}
                      />
                      </td>
                      <td>{bp.mk_number}</td>
                      <td>{bp.species}</td>
                      <td>{bp.matrix}</td>
                      <td>{bp.extraction_method}</td>
                      <td>{bp.internal_standard}</td>
                      <td>{bp.chromatography}</td>
                      <td>{bp.polarity}</td>
                  </tr>
                  </tbody>
              </table>
              <hr></hr>
          </div>
        ))}
      </div>
    );
  } else {
    return (
      <div ref={resultsRef}>
          <div style={{ display: 'flex', justifyContent: 'space-between'}}>
            <div>
              <h1> No Results Found</h1>      
            </div>
          </div>
      </div>
    );
  }

}
