import React, { useState } from 'react';
import LinearProgress from '@mui/material/LinearProgress';
import './upload.css';
import Dialog from '@mui/material/Dialog';
import LogoSmall from './logoSmall';
import DialogContent from '@mui/material/DialogContent';
import Button from 'react-bootstrap/Button';
import { DialogActions, DialogTitle } from '@mui/material';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';


function Upload() {
    const [progress, setProgress] = useState(0);
    const [showProgress, setShowProgress] = useState(false);
    const [message, setMessage] = useState('');
    const [confirmation, setConfirmation] = useState('');

    const [expanded, setExpanded] = useState(false);

    const [acceptedFile, setAcceptedFile] = useState(null);

    const handleClickOpen = () => {
        setExpanded(true);
    };

    const handleClose = () => {
        setProgress(0);
        setShowProgress(false);
        setMessage('');
        setConfirmation('');
        setExpanded(false);
    };

    const upload = async () => {
        if (!acceptedFile) {
            setMessage('Please add a document to upload.');
            return;
        }

        setMessage('');
        setConfirmation('');
        let url = 'http://54.90.86.188/upload';
        const form = new FormData();
        form.append('file', acceptedFile);
        setShowProgress(true);

        try {
            const response = await axios.post(url, form, {
                headers: { 'Content-Type': 'undefined' },
                timeout: 6000000,
                onUploadProgress: (progressEvent) => {
                const { loaded, total } = progressEvent;
                const percentageProgress = Math.round((loaded * 50) / total);
                setConfirmation('Uploading...');
                setProgress(percentageProgress);
                },
            });

            setConfirmation('Parsing...');
            setProgress(100);
            setConfirmation('File Upload Complete.');
            console.log(response);

        } catch (error) {
            setMessage('An error occurred while uploading the file. Please try again.');
            console.log(error);
            setConfirmation('');
            setProgress(0);
            setShowProgress(false);
        }
    };

    /*
    const buildResults = () => {
        var results = [];
        if(parsed != null) {
            for(var i = 0; i < parsed.length; i++) {
                var obj = parsed[i];
                for (var key in obj){
                    var val = obj[key];
                    results.push(<tr><td>{key}</td><td><TextareaAutosize defaultValue={val} style={{border:"none"}}></TextareaAutosize></td></tr>);
                }
            }
        }
        //console.log(results);
        return results;
    }
    */

    const { getRootProps, getInputProps } = useDropzone({
        accept: 'application/pdf',
        onDrop: (acceptedFiles) => {
            const file = acceptedFiles[0];
            const fileNamePattern = /^BP_\d{4}\.pdf$/;
            if (fileNamePattern.test(file.name)) {
                setAcceptedFile(file);
                setMessage('');
            } else {
                setMessage('Invalid file name. Please upload a file in the format: BP_####.pdf');
            }
        },
    });

    return(
        <div>
            {/* ... */}
            <Dialog open={expanded} onClose={handleClose} fullWidth maxWidth="sm">
                <DialogTitle>
                {/* ... */}
                </DialogTitle>
                <DialogContent>
                <section>
                    <div id="content" {...getRootProps()}>
                    <input {...getInputProps()} />
                    <div className="drag-area">
                        <div className="icon">
                        <i className="fas fa-cloud-upload-alt"></i>
                        </div>
                        <header>Drag & Drop to Upload PDF</header>
                        <span>or</span>
                        <button>Browse PDF</button>
                    </div>
                    </div>
                    <aside>
                    <ul>
                        {acceptedFile && (
                        <li key={acceptedFile.path}>
                            {acceptedFile.path} -{' '}
                            {(acceptedFile.size / 1000000).toFixed(2)} MB
                        </li>
                        )}
                    </ul>
                    </aside>
                </section>
                {showProgress && <LinearProgress variant="determinate" value={progress} />}
                <p style={{ textAlign: 'center' }}>{confirmation}</p>
                <p style={{ color: 'red', textAlign: 'center' }}>{message}</p>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={upload} color="primary">
                        Upload
                    </Button>
                </DialogActions>
            </Dialog>
            { /*


            <Dialog open={expandedResults} onClose={handleClose} fullWidth maxWidth="sm">
            <DialogTitle>
                    <div id='logoSmall'>
                        <LogoSmall/>
                    </div>
                </DialogTitle>
                <div class = "resultsHead">
                <header>Document Review</header>
                <header2>Please Confirm the Parsed Information Below</header2>
                <subtitle>double click any field to edit</subtitle>
                </div>
                <div class = "resultsList">
                <table>{buildResults()}</table>
                </div>
                <div class = "resultsBtn">
                <button onClick={handleResultsClose}>Confirm</button>
                </div>

            </Dialog>
            */
            }  
        </div>
    )

}

export default Upload;
