import './App.css';
import Logo from './logo';
import SearchBar from './searchBar';
import Upload from './upload';
import "bootstrap/dist/css/bootstrap.min.css";
import Results from './results';
import { useState } from 'react';

function App() {
  const [showResults, setShowResults] = useState(false);
  const [resultsData, setResultsData] = useState(null);

  const renderResults = (data) => { //we will pass this function to the search bar component
    console.log("Received data in App:", data);
    setResultsData(data);
    setShowResults(true);
  };

  return (
    <div className="App">
      <div className='topBar'>
        <div id='logo'>
          <Logo/>
        </div>
        <div id="title-box">
          <div id="title-text">Bioanalytical Procedure Database System</div>
          <div id="sub-text">The Data Mine - Corporate Partners: Merck NLP</div>
        </div>
        <div id='search'>
          <SearchBar renderResults={renderResults}/>
        </div>
        <div id='upload'>
          <Upload/>
        </div>
      </div>
      <div id='results'>
        {showResults && <Results resultsData={resultsData} />}
      </div>
    </div>
  );
}

export default App;
