import React, { useState } from 'react';
import axios from 'axios';
import './searchBar.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';


function SearchBar({ renderResults }) {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();
  const [message, setMessage] = useState('');

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      renderResults(null);
      const response = await axios.post("http://3.85.227.114/query", {
        term: searchTerm,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = response.data.map(result => result.map(item => JSON.parse(item)));
      console.log(data);
      renderResults(data);
      navigate('/results');
    } catch (error) {
      console.error("Error making POST request:", error);
      setMessage("Error occured while searching. Please try again.");
    }
  };

  return (

    <div className="searchBar">
      <div id="bar">
        <InputGroup>
          <Form.Control onChange={handleSearchTermChange} />
          &nbsp;
          <Button variant="dark"  onClick={handleSubmit}>
            Search
          </Button>
        </InputGroup>
      </div>
      <p>message</p>
    </div>
  );
}

export default SearchBar;
