import React, { useState } from 'react';
import axios from 'axios';
import './searchBar.css';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import { useHistory } from 'react-router-dom';

function SearchBar({ renderResults }) {
  const [searchTerm, setSearchTerm] = useState('');
  const history = useHistory();

  const handleSearchTermChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post("http://54.90.86.188/query", {
        term: searchTerm,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = response.data.map(result => result.map(item => JSON.parse(item)));
      console.log(data);
      renderResults(data);
      history.push('/results'); // Navigate to the results page
    } catch (error) {
      console.error("Error making POST request:", error);
    }
  };

  return (
    <div className="searchBar">
      <div id="bar">
        <InputGroup>
          <Form.Control onChange={handleSearchTermChange} />
          &nbsp;
          <div id="submit">
            <input id="submit-btn" type="submit" value="Search" onClick={handleSubmit} />
          </div>
        </InputGroup>
      </div>
    </div>
  );
}

export default SearchBar;
